import React from 'react';
import Button from 'react-bootstrap/Button'
import ButtonGroup from 'react-bootstrap/ButtonGroup'
import './App.css';

class AnswerForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      handleSubmit: props.handleSubmitfuckit,
      placeholder: ''
    };
  }

  handleChange(e) {
    this.setState({ value: e.target.value });

  }

  clear() {
    this.setState({ value: "" })
  }

  render() {
    return (
      <form onSubmit={(e, myForm) => this.props.handleSubmit(e, this)}>
        <input type="text"
          className="answer"
          placeholder={this.state.placeholder}
          value={this.state.value}
          onChange={(e) => this.handleChange(e)} />
      </form>
    );
  }
}

class NewWordForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      handleSubmit: props.handleSubmit,
      placeholder: props.placeholder
    };
  }

  handleChange(e) {
    this.setState({ value: e.target.value });

  }

  clear() {
    this.setState({ value: "" })
  }

  render() {
    return (
      <form onSubmit={(e, myForm) => this.props.handleSubmit(e, this)}>
        <input type="text"
          className="newWord"
          placeholder={this.state.placeholder}
          value={this.state.value}
          onChange={(e) => this.handleChange(e)} />
      </form>
    );
  }
}

class Card extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      word: "vous",
      definition: "you - formal/plural",
      sBegin: "Est-ce que",
      sEnd: "etes prets?",
      sTrans: "Are you ready?",
      strength: 0,
      dummyDataIdx: 0,
      renderState: 0,
      placeholder: {}
    }
  }

  setStateStudy(e) {
    e.preventDefault();
    this.setState({
      renderState : 1
    })
  }

  setStateAdd(e) {
    e.preventDefault();
    this.setState({
      renderState : 2
    })
  }

  getNextData() {
    const dummyData = [
      {
        word: "vous",
        definition: "you - formal/plural",
        sBegin: "Est-ce que",
        sEnd: "etes prets?",
        sTrans: "Are you ready?",
      },
      {
        word: "faut",
        definition: "need, have to - impersonal",
        sBegin: "il",
        sEnd: "que j'y aille",
        sTrans: "I have to go",
      },
      {
        word: "sain",
        definition: "healthy, in good health",
        sBegin: "il est",
        sEnd: "sain et sauf",
        sTrans: "he is safe and sound",
      }
    ]

    const nextIdx = (this.state.dummyDataIdx + 1) % dummyData.length;
    this.setState({
      word: dummyData[nextIdx].word,
      definition: dummyData[nextIdx].definition,
      sBegin: dummyData[nextIdx].sBegin,
      sEnd: dummyData[nextIdx].sEnd,
      sTrans: dummyData[nextIdx].sTrans,
      dummyDataIdx: nextIdx
    })
    // DO NOT DELETE THIS COMMENT 
    // this is how the tool will communicate 
    // with the backend once it is implemented
    // fetch('http://localhost:8080/next')
    //   .then(res => res.json())
    //   .then((data) => {
    //     this.setState({
    //       placeholder: data,
    //       word: data.term,
    //       definition: data.definition,
    //       sBegin: data.sbegin,
    //       sEnd: data.send,
    //       sTrans: data.translation,
    //       strength : data.strength
    //     })
    //   }).catch(console.log)
  }

  onFormSubmit(e, myForm) {
    e.preventDefault();
    console.log(myForm.state.value)
    let guess = myForm.state.value;
    if (guess === this.state.word) {
      //get the next word
      myForm.setState({
        value: "",
        placeholder: ""
      })
      this.getNextData();

    }
    else {
      console.log(this.state.word + " is not " + guess)
      myForm.setState({
        value: "",
        placeholder: this.state.word
      })
    }
    //myForm.setState({value: ""})
  }

  onNewWordSubmit(e, myForm) {

  }

  onNewSentenceSubmit(e, myForm) {

  }

  onNewDefinitionSubmit(e, myForm) {

  }

  onNewTranslationSubmit(e, myForm) {

  }


  getBlank() {
    let retString = "";
    for (let i = 0; i < this.state.word.length; i++) {
      retString += "_";
    }
    return retString;
  }

  renderStrength() {
    let retstring = "";
    switch (this.state.strength) {
      case 0:
        retstring = "";
        break;
      case 1:
        retstring = "New word"
        break;
      case 2:
        retstring = "Weak word"
        break;
      case 3:
        retstring = "Recognized word"
        break;
      case 4:
        retstring = "Strong word"
        break;
      case 5:
        retstring = "Known word"
        break;
      default:
        break;
    }

    return (
      <>
        {retstring}
      </>
    )
  }

  renderWelcomeState() {
    return (
      <div className="welcomeScreen">
        <h1>Welcome</h1>
        <ButtonGroup>
          <Button variant="dark" onClick={(e) => this.setStateStudy(e)}>
            Study
          </Button>
          <Button variant="dark" onClick={(e) => this.setStateAdd(e)}>
            Add Words
          </Button>
          </ButtonGroup>
      </div>
    );

  }

  renderStudyState() {
    return (
      <>
        <div className="sentence">
          {this.state.sBegin + " "}
          {this.getBlank()}
          {this.state.sEnd}
        </div>
        <div className="translation">
          {this.state.sTrans}
        </div>
        <div className="definition">
          {this.state.definition}
        </div>
        <AnswerForm handleSubmit={(e, myForm) => this.onFormSubmit(e, myForm)} />
        {this.renderStrength()}

        <Button variant="dark" onClick={(e) => this.setStateAdd(e)}>
            Add Words
          </Button>
      </>
    );
  }

  renderAddState() {
    return (
      <div className="welcomeScreen">

        <NewWordForm placeholder="Word" handleSubmit={(e, myForm) => this.onFormSubmit(e, myForm)} />
        <NewWordForm placeholder="Definition" handleSubmit={(e, myForm) => this.onFormSubmit(e, myForm)} />
        <NewWordForm placeholder="Example Sentence (optional)" handleSubmit={(e, myForm) => this.onFormSubmit(e, myForm)} />
        <NewWordForm placeholder="Example Translation (optional)" handleSubmit={(e, myForm) => this.onFormSubmit(e, myForm)} />
        <ButtonGroup>
          <Button variant="dark" onClick={(e) => this.setStateStudy(e)}>
            Study
          </Button>
          </ButtonGroup>
      </div>
    );

  }

  renderBasedOnState() {
    switch (this.state.renderState) {
      case 0:
        return this.renderWelcomeState();
      case 1:
        return this.renderStudyState();
      case 2:
        return this.renderAddState();
      default:
        return this.renderWelcomeState();
    }
  }

  render() {
    return (
      <div className="StudyCard">
        {this.renderBasedOnState()}
      </div>
    );
  }

  componentDidMount() {
    fetch('http://localhost:8080/next')
      .then(res => res.json())
      .then((data) => {
        this.setState({
          placeholder: data,
          word: data.term,
          definition: data.definition,
          sBegin: data.sbegin,
          sEnd: data.send,
          sTrans: data.translation,
          strength: data.strength
        })
      }).catch(console.log)
  }


}

function App() {
  return (
    <div className="App">
      <header className="App-header">
        <Card />
      </header>
    </div>
  );
}

export default App;
