import React from 'react';
import Button from 'react-bootstrap/Button'
import ButtonGroup from 'react-bootstrap/ButtonGroup'
import DropdownButton from 'react-bootstrap/DropdownButton'
import Dropdown from 'react-bootstrap/Dropdown'
import './App.css';

class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {render:"select", side:"Front", front:"", back:"", deck:"", card:[{front:"Est-ce que ____etes prets?", back:"vous"}, {front:"il ____que j'y aille", back:"faut"}, {front:"il est ____et sauf", back:"sain"}], index:0, answer:"", placeholder:"Answer"};    
    }

    render() {
        return <div className="App"><header className="App-header">
        {this.renderSwitch()}
        </header></div>    
    }

    renderSwitch() {
        switch(this.state.render) {
            case "study": return this.renderStudy()
            case "deck": return this.renderDeck()       
            case "word": return this.renderWord()         
            case "select": return this.renderSelect()    
            default: return <p>error</p>
        }    
    }

    renderStudy() { return <>
        <p>{this.state.card[this.state.index].front}</p>
        <form onSubmit=
                        {(e) => { e.preventDefault(); if(e.target[0].value.toLowerCase() == this.state.card[this.state.index].back.toLowerCase()) 
                                {this.setState({index:(this.state.index+1)%3, answer:"", placeholder:"Answer"})} 
                                else { this.setState({answer:"", placeholder:"Incorrect"}) } } }>
                <input type="text" value={this.state.answer} onChange={(e) => this.setState({answer:e.target.value})} placeholder={this.state.placeholder} />
                 </form>
        <p /><button onClick={() => this.setState({render: "word"})}>Add card to deck</button>
    </>
    }

    renderDeck() { return <>
        <p>Make a new Deck</p>
        <h3>Enter the title of your Deck</h3>
        <form onSubmit={(e) => { e.preventDefault(); this.setState({render: "word"}) }} >
                <input onChange={(e) => { e.preventDefault(); this.setState({deck:e.target.value}) }} type="text" placeholder="French, Spanish, etc..." />
        </form>
        </>    
    }

    renderWord() { return  <>
        <p>Add a Card to {this.state.deck}</p>
        <ButtonGroup>
        <Button variant={this.state.side=="Front"? "light" : "dark"} onClick={() => this.setState({side: "Front"})}>Front</Button>
        <Button variant={this.state.side=="Back"? "light" : "dark"} onClick={() => this.setState({side: "Back"})}>Back</Button></ButtonGroup>
        <form onSubmit={(e) => {e.preventDefault(); 
                                if(this.state.side=="Front") { this.setState({side:"Back"})}
                                else { if(this.state.front==""||this.state.back=="") {this.setState({side:"Front"})} else { this.setState({side:"Front", front:"", back:""}); alert("the card has been added to the selected deck")}}} }
        width="300px">
                <input type="text" value={this.state.side =="Front"? this.state.front : this.state.back }
                                   onChange={(e) => { e.preventDefault();
                                                     if(this.state.side=="Front") {this.setState({front:e.target.value})} 
                                                    else {this.setState({back:e.target.value})} }}
                placeholder={this.state.side + " of Card"} />
        </form>     
        <p /><p />
        <div> <Button onClick={() => this.setState({render: "select"})}>Study another Topic</Button>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<Button onClick={() => this.setState({render: "study"})}>Study this Deck</Button>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
              <Button onClick={() => this.setState({render: "deck"})}>Make another Deck</Button> </div>

        </>
    }





    renderSelect(){ 
        return <>
               <p>Choose the Subject</p>
               <div class="scroll">
               <Button onClick={() => this.setState({render: "study", deck:"French"})}>French</Button><p />
               <Button>Spanish</Button><p />
               <Button>Italian</Button><p />
               <Button>German</Button><p />
               <Button>Latin</Button><p />
               <Button>Java</Button><p />
               <Button>Japanese</Button><p />
               <Button>Javanese</Button><p />
               <Button>Lorem</Button><p />
               <Button>Ipsum</Button><p />
               <Button>Virology</Button><p />
               <Button>Topic</Button><p />
               <Button>French</Button><p />
               <Button>French</Button><p />
               <Button>French</Button><p />
               </div> 
                <p />
               <form onSubmit={(e) => alert("The list of decks is filtered by the search term, case-independant")} >
                <input type="text" placeholder="Search" />
                 </form>
                <p />
               <Button onClick={() => this.setState({render: "deck"})}>Make a new deck</Button>
               </>
    }

}

export default App;
