import React from 'react';
import Button from 'react-bootstrap/Button'
import ButtonGroup from 'react-bootstrap/ButtonGroup'
import DropdownButton from 'react-bootstrap/DropdownButton'
import Dropdown from 'react-bootstrap/Dropdown'
import './App.css';

class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {render:"select"};    
    }

    render() {
        return <div className="App"><header className="App-header">
        {this.renderSwitch()}
        </header></div>    
    }

    renderSwitch() {
        switch(this.state.render) {
            case "study": return this.renderStudy()
            case "deck": return this.renderDeck()       
            case "word": return this.renderWord()         
            case "select": return this.renderSelect()    
            default: return <p>error</p>
        }    
    }

    renderStudy() { return <p>afgounDeck</p>
    }

    renderDeck() { return <p>Deck</p>}

    renderWord() { return <p>Word</p>}

    renderSelect(){ 
        return <>
               <p>Choose the Subject</p>
               <div class="scroll">
               <Button onClick={() => this.setState({render: "study"})}>French</Button><p />
               <Button>Spanish</Button><p />
               <Button>Italian</Button><p />
               <Button>German</Button><p />
               <Button>Latin</Button><p />
               <Button>Java</Button><p />
               <Button>Japanese</Button><p />
               <Button>Javanese</Button><p />
               <Button>Lorem</Button><p />
               <Button>Ipsum</Button><p />
               <Button>Virology</Button><p />
               <Button>Topic</Button><p />
               <Button>French</Button><p />
               <Button>French</Button><p />
               <Button>French</Button><p />
               </div> 
                <p />
               <form onSubmit={(e) => alert("The list of decks is filtered by the search term")} >
                <input type="text" placeholder="Search" />
                 </form>
                <p />
               <Button variant="dark">Make a new deck</Button>
               </>
    }

}

export default App;
