import React from 'react';
import Button from 'react-bootstrap/Button'
import ButtonGroup from 'react-bootstrap/ButtonGroup'
import DropdownButton from 'react-bootstrap/DropdownButton'
import Dropdown from 'react-bootstrap/Dropdown'
import './App.css';

class App extends React.Component {
    constructor(props) {
        super(props)
        this.state = {render:"select"};    
    }

    render() {
        return <div>
        {this.renderSwitch()}
        </div>    
    }

    renderSwitch() {
        switch(this.state.render) {
            case "study": return this.renderStudy()
            case "deck": return this.renderDeck()       
            case "word": return this.renderWord()         
            case "select": return this.renderSelect()    
        }    
    }

    renderStudy() { return <><p>Study</p><button onClick={(e) => this.setState({render: "deck"})} /> </>}
    renderDeck() { return <p>Deck</p>}
    renderWord() { return <p>Word</p>}
    renderSelect(){ return<p>select</p> }

}

export default App;
